    if (diff.isEmpty()) {
      return;
    }
    StringBuilder sb = new StringBuilder();
    for (NodeDifference nd : diff) {
      sb.append(nd.print());
    }
    Assert.fail("Transformation output model doesn't match reference one:\n" + sb.toString());
